import React from 'react'
import './schimify.css'
import fetch from 'node-fetch';
import cheerio from 'cheerio';
//import hirationblogdata from './parser.js'

export default function schimify() {
    const getRawData = (URL) => { 
        return fetch(URL)
           .then((response) => response.text())
           .then((data) => {
              return data;
           });
     };
    function handleChange(event) {
        console.log(event.target.value);
        const hirationblogdata = async () => {
            const hirationblogdata = await getRawData(event.target.valueL);
             const $ = cheerio.load(hirationblogdata);
           let title = $('title');
           console.log(title.text());

      }
      hirationblogdata()}
    return (
        <div>
            <div className="allwrap">
                <h1 className="schimify">Schimify</h1>
                <div className="wrapper">
                    <div className="left"> <input type="url" className='URL' name="url" id="url" placeholder='Paste the URL' onChange={handleChange} />
                        <input type="text" name="slug" id="slug" className='slug' placeholder='Paste the slug' />
                        <input type="text" name="keyword" id="keyword" className='keyword' placeholder='Paste the keyword' />
                        <button id="submit" className='sumbit'> Create the Schema </button>
                    </div>
                    <div className="right">
                        <div id="output" className="output"> 
                            Here is your schema code


                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
